import React, { Component } from 'react'
import axios from 'axios';
import InsertData from './InsertData'
export default class ReadData extends Component {

    constructor() {
        super();
        this.state = {
            task: [
                {
                    'id': 0,
                    'task': 'Learn React',
                    'status': 'active',
                }

            ]


        }
        this.updateData=this.updateData.bind(this)
    }

    componentDidMount() {

        const arr = this.state.task;
        axios.get('http://localhost/laravelapi/ReadData')
            .then((res) => {

                const obj = res.data;
                obj.unshift(arr[0]);
                console.log(obj)
                this.setState({
                    task: obj


                })

            })
            .catch(err => {
                console.log(err);
            })

    }


    updateData=()=>{
        const arr = this.state.task;
        axios.get('http://localhost/laravelapi/ReadData')
            .then((res) => {

                const obj = res.data;
                obj.unshift(arr[0]);
                console.log(obj)
                this.setState({
                    task: obj


                })

            })
            .catch(err => {
                console.log(err);
            })
    }


    render() {
        const tasks = this.state.task.map((task) =>
            <li className="list-group-item" key={task.id}> Task name : {task.task}. Status : {task.status}.</li>
        );
        return (
            <div>



                <div className="row">
                    <div className="col-md-4 offset-md-1">

                        <div >

                            <ul className="list-group">
                                <li className="list-group-item active text-center" > Task list </li>

                                {tasks}

                            </ul>


                        </div>
                    </div>
                    <div className="col-md-4 offset-md-1">
                        <InsertData sentData={this.updateData} />
                    </div>

                </div>


            </div>
        )
    }
}
