import React, { Component } from 'react'
import axios from 'axios';

export default class ReadData extends Component {

    constructor() {
        super();
        this.state = {
            task:[
                {
                    'id': 0,
                    'task': 'Learn React',
                    'status': 'active',
                }
            
            ] 
               

        }
    }

    componentDidMount() {
        
       const arr = this.state.task;
        axios.get('http://localhost/laravelapi/ReadData')
        .then((res)=>{
            
            const obj = res.data;
            obj.unshift(arr[0]);
            console.log(obj)
            this.setState({
            task : obj
            

            })
            
        })
        .catch(err=>{
            console.log(err);
        })
        
    }


    render() {
        const tasks = this.state.task.map((task) =>
            <li className="list-group-item" key={task.id}> Task name : {task.task}. Status : {task.status}.</li>
        );
        return (
            <div>
               

                <div >
                    
                        <ul className="list-group">
                        <li className="list-group-item active text-center" > Task list </li>

                        {tasks}  

                        </ul>
                        
                    
                </div>


                <form method="post" onSubmit={this.SaveData} >
                <div className="form-group row">
                    <div className="col-md-3">
                        <h5>Your Task </h5>

                    </div>

                    <div className="col">
                        <input type="text" onChange={this.handelChange} name="task" id="" className="form-control" placeholder="" aria-describedby="helpId" />

                    </div>

                </div>
                <div className="row">
                    <div className="col-md-3">
                        <h5>Task Status</h5>

                    </div>

                    <div className="col">
                        <input type="text" name="status"  onChange={this.handelChange} id="" className="form-control" placeholder="" aria-describedby="helpId" />

                    </div>
                </div> <br/>
                <div className="btn-group col-md-2 offset-md-4" role="group" aria-label="">
                    <button type="submit" className="btn btn-primary">Save</button>
                </div>
                </form>


            </div>
        )
    }
}
