import axios from 'axios';

export default class ReadData extends Component {

    constructor() {
        super();
        this.state = {
            task:[
                {
                    'id': 0,
                    'task': 'Learn React',
                    'status': 'active',
                }
            
            ] 
               

        }
    }

    componentDidMount() {
        
       const arr = this.state.task;
        axios.get('http://localhost/laravelapi/ReadData')
        .then((res)=>{
            
            // const obj = Object.assign({},arr[0],res.data)
            // console.log(obj)
            const obj = 
            console.log(arr)
            this.setState({
            task : res.data
            

            })
            console.log(this.state.task)
        })
        .catch(err=>{
            console.log(err);
        })
        
    }


    render() {
        const tasks = this.state.task.map((task) =>
            <li className="list-group-item" key={task.id}> Task name : {task.task}. Status : {task.status}.</li>
        );
        return (
            <div>
               

                <div className="row">
                    <div className="col-md-6 offset-md-3">
                        <ul className="list-group">
                        <li className="list-group-item active text-center" > Task list </li>

                        {tasks}  

                        </ul>
                        
                    </div>
                </div>


            </div>
        )
    }
}
