import React, { Component } from 'react'

export default class ReadData extends Component {

    constructor() {
        super();
        this.state={
            task:[
            {'id':1,
              'task':'Learn React',
              'status':'active',
            }
        ],
          
        }
    }

componentDidMount(){
    this.setState({
        name: this.state.name + ' Ahmed',   
    })
}


  render() {
      const tasks=this.state.task.map((task)=>
      <li key={task.id}> Task name : {task.task}. Status : {task.status} .</li> 
      );
    return (
      <div>
      <h1>hello , {this.state.name}</h1>
      {tasks}
      </div>
    )
  }
}
